(768, 232, 'thong_so_ky_thuat', '<table border=\"1\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td><strong>Chỉ tiêu</strong></td>\r\n<td><strong>Thông số</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Khối lượng thể tích</td>\r\n<td>450 - 850 kg/m3</td>\r\n</tr>\r\n<tr>\r\n<td>Cường độ nén trung bình</td>\r\n<td>2.5 - 4.5 MPa</td>\r\n</tr>\r\n<tr>\r\n<td>Hệ số dẫn nhiệt</td>\r\n<td>0.11 - 0.22 W/m0K</td>\r\n</tr>\r\n<tr>\r\n<td>Độ cách âm</td>\r\n<td>40 - 47 db</td>\r\n</tr>\r\n<tr>\r\n<td>Sai lệch kích thước (dài, dày, cao)</td>\r\n<td>±3 (mm) ±2 (mm) ±2 (mm)</td>\r\n</tr>\r\n<tr>\r\n<td>Chống cháy</td>\r\n<td>Đạt giới hạn chịu lửa EI 240 phút</td>\r\n</tr>\r\n<tr>\r\n<td>Độ co khô</td>\r\n<td>&lt; 0.2 mm/m</td>\r\n</tr>\r\n</tbody>\r\n</table>'),
